import React, { useState } from 'react'
import MovieList from './MovieList'

const App = () => {
    const [movies, setMovies] = useState([
        {
            title: "Chernobly", 
            description: "The events recount the circumstances of the explosion of the Chernobyl nuclear reactor in April 1986 in the Soviet Socialist Union, which became one of the worst human disasters in the twentieth century.", 
            posterURL: "https://www.themoviedb.org/t/p/original/4cPTQbF2GL59j2YGeUaBg3yvzJw.jpg", 
            rating: 9.4
        },
        {
            title: 'Cosmos', 
            description: "An exploration of our discovery of the laws of nature and coordinates in space and time.", posterURL: "Cosmos.jpg", rating: 9.3
        },

        {
            title: 'MR. ROBOT', 
            description: "The series tells the story of a young man who suffers from social anxiety disorder. Working as a computer programmer in the morning and a hacker in the evening, he is assigned to work by a mysterious man who calls himself 'Mr. Robot' to infiltrate large corporations they believe are corrupting society.", 
            posterURL: "https://m.media-amazon.com/images/M/MV5BMzgxMmQxZjQtNDdmMC00MjRlLTk1MDEtZDcwNTdmOTg0YzA2XkEyXkFqcGdeQXVyMzQ2MDI5NjU@._V1_FMjpg_UX1000_.jpg", 
            rating: 8.5

        },

        {
            title: 'Planet Earth', description: "Emmy Award-winning, 11 episodes, five years in the making, the most expensive nature documentary series ever commissioned by the BBC, and the first to be filmed in high definition.", posterURL: "Planet Earth.jpg", rating: 9.4
        },
        {
            title: 'Prison Break', description: "An innocent man is imprisoned and sentenced to death, and his only hope now is in his younger brother, who commits a crime in order to send himself to prison and devises a plan for their escape together, in addition to some other civilians in prison, they encounter a series of interesting and exciting problems and dilemmas, and they try to solve them in order to implement the plan, no matter what it costs them.", posterURL: "Prison Break.jpg", rating: 8.3
        },
        {
            title: 'Roman Empire', description: "Chronicles some of the most famous leaders of the Roman Civilization.", posterURL: "Roman Empire.jpg", rating: 6.9
        }])
    return (
        <div>
            <MovieList movie={movies} />
        </div>
    )
}

export default App
