import React,{useState } from "react";
import "./"



const AddMovies=({addMovies})=>{
    const[newMovie,setnewmovie]= useState({title: "",description:"",posterURL:"",rate:""})
const handleInputchange = (e)=>{
const {name,value}= e.target;
    setnewmovie({...newMovie,[name]: value});
}
const handlesubmit = (e)=> {
    e.preventDefault();
    addMovies(newMovie);
    setnewmovie({ title:"", description:"", posterURL:"", rate:""})
}
    return (
        <form onSubmit={handlesubmit}>
        <div className="form">
            <div className="title">Add New Movie</div>
            <div className="subtitle">From Here</div>
            <div className="input-container ic1">
            <input id="firstname" className="input" type="text" name="title" placeholder="Title" value={newMovie.title} onChange={handleInputchange} required/>
            <div className="cut" />
              <label htmlFor="firstname" className="placeholder">
                Description
              </label>
            <input  type="description" name="description" placeholder="description" value={newMovie.description} onChange={handleInputchange} required/>
            <div className="cut" />
              <label htmlFor="firstname" className="placeholder">
               Image
              </label>
            <input  type="text" name="posterURL" placeholder="posterURL" value={newMovie.posterURL} onChange={handleInputchange} required/>
            <div className="cut" />
              <label htmlFor="firstname" className="placeholder">
                rate
              </label>
            <input  type="number" name="rate" placeholder="rate" value={newMovie.rate} onChange={handleInputchange} required/>
            <button type="text" className="submit">
              submit
            </button>
            </div>
            </div>
            </form>
      
        
          
          
    );
    } 
export default AddMovies
